<template>
  <div class="headbar">
    <div class="transparent-backgound"
    v-bind:style="{ 'left': positionBackground }"
    >&nbsp;</div>
    <div class="menu-links"
    v-bind:style="{ 'left': positionMenu }"
    >
      <div
      v-bind:style="{ 'left': positionLinks }"
      class="links">
         <a class="menu-link" href="https://ssapnapier.github.io">Home</a>
         <a class="menu-link" href="https://ssapnapier.github.io/shop/">Shop</a>
         <a class="menu-link" href="https://ssapnapier.github.io/about-us/">About us</a>
         <a class="menu-link" href="https://ssapnapier.github.io/contact/">Contact</a>
      </div>
    </div>
    <div class="menu"
      v-bind:style="{ 'left': positionImg }">
      <img src="data:image/svg+xml;base64,"
      v-on:click="changePosition"
      alt="">
    </div>
    <div class="logo">
      <a href="https://ssapnapier.github.io">
      <img class="image" src="data:image/svg+xml;base64," alt="">
    </a>
    </div>
    <div class="basket">
      <img   v-on:click="activeModal" src="data:image/svg+xml;base64," alt="">
      <h4 class="numbers">{{ evt }}</h4>
    </div>
    </div>
  </div>
</template>

<script>
// import Vue from 'vue'
import Shop from './Shop.vue';
// Vue.prototype.$appName = 0;

export default {
  props: ["evt"],
  data() {
    return {
          windowWidth: 0,
          logo: 'static/logo.svg',
          positionMenu: "-50%",
          positionImg: "0px",
          positionLinks: "-200%",
          positionBackground: "-110%",
          displayActive: "none",
          color: "black",
          evt: 0
        }
  },
   mounted:function(){
     this.evt = localStorage.clickcount;
     // this.evt = ++this.msg;
     // this.evt = --this.msg;
   },
  methods:{
     changePosition: function() {
       if (this.positionMenu == '-50%') {
         this.positionMenu = '-40px';
       } else {
         this.positionMenu = '-50%'
       }
       if (this.positionImg == '0px') {
         this.positionImg = '15%';
       } else {
         this.positionImg = '0px'
       }
       if (this.positionBackground == '-110%') {
         this.positionBackground = '-3%';
       } else {
         this.positionBackground = '-110%'
       }
       if (this.positionLinks = "-160%") {
         this.positionLinks = "30px";
       }else {
         this.positionLinks = "-160%";
       }
     },
     activeModal: function() {
       if(this.displayActive == "none") {
         this.displayActive = "block";
         localStorage.cli = 0;
       } else {
         this.displayActive = "none";
       }
     }
  },
  computed: {
    styles: function() {
      return {
        color: this.fontColor
      }
    }
  },
  components: {
    'app-input': Shop
  }
}
</script>

